package week1.day2;

public class ConvertPostivetoNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=-40;
		
		if(n>=0) {
			
			System.out.println("The Number is not Negative : ");
			
		} else 
			
			n=n*(-1);
		
		   System.out.println("The Number is coverted to Postive: " +n);
		
		
	}

}
